document.querySelectorAll('.currency').forEach(function (dropDownWrapper) {
	const dropDownBtn = dropDownWrapper.querySelector('.currency__button');
	const dropDownList = dropDownWrapper.querySelector('.currency__body');
	const dropDownListItems = dropDownList.querySelectorAll('.currency__item');

	// Клик по кнопке. Открыть/Закрыть select
	dropDownBtn.addEventListener('click', function (e) {
		dropDownList.classList.toggle('visible');
		this.classList.toggle('active');
	});

	// Выбор элемента списка. Запомнить выбранное значение. Закрыть дропдаун
	dropDownListItems.forEach(function (dropDownListItem) {
		dropDownListItem.addEventListener('click', function (e) {
			e.stopPropagation();
			dropDownBtn.innerText = this.innerText;
			if (this.classList.contains('not-visible')) {
				this.classList.remove('not-visible');
			} else {
				const activeLink = dropDownList.querySelector('.currency__item.not-visible');
				if (activeLink) {
					activeLink.classList.remove('not-visible');
				}
				this.classList.add('not-visible');
			}
			dropDownBtn.focus();
			dropDownList.classList.remove('visible');
			dropDownBtn.classList.remove('active');
			if (e.target.classList.contains('label')) {
				dropDownBtn.classList.remove('selected');
			} else {
				dropDownBtn.classList.add('selected');
			}
		});
	});

	// Клик снаружи дропдауна. Закрыть дропдаун
	document.addEventListener('click', function (e) {
		if (e.target !== dropDownBtn) {
			dropDownBtn.classList.remove('active');
			dropDownList.classList.remove('visible');
		}
	});

	// Нажатие на Tab или Escape. Закрыть дропдаун
	document.addEventListener('keydown', function (e) {
		if (e.key === 'Tab' || e.key === 'Escape') {
			dropDownBtn.classList.remove('active');
			dropDownList.classList.remove('visible');
		}
	});
});
//FIX input search
let searchForm = document.querySelector('.search');
let btnCloze = document.querySelector('.search__close');
let btns = document.querySelector('.search__buttons');
let btnOpen = document.querySelector('.search__btn-open');
let formWrapper = document.querySelector('.search__wrapper');
let btnReset = document.querySelector('.form-search__reset');
let inputs = document.querySelectorAll('.input-search').forEach(function (inputs) {
	inputs.addEventListener('focus', function () {
		searchForm.classList.add('focus');
	}, true);
	inputs.addEventListener('blur', function () {
		if (this.value === '') {
			searchForm.classList.remove('focus');
		}
	}, true);
	inputs.addEventListener('input', function () {
		searchForm.classList.add('active');
		if (this.value === '') {
			searchForm.classList.remove('active');
		}
	})
	//FIX input open/cloze/reset

	btnOpen.addEventListener('click', function () {
		btns.classList.add('active-btn');
		formWrapper.classList.add('visible');

	})
	btnCloze.addEventListener('click', function () {
		btns.classList.remove('active-btn');
		formWrapper.classList.remove('visible');
		searchForm.classList.remove('active');
		if (window.innerWidth <= 990) {
			searchForm.classList.remove('focus');
		}
		inputs.value = "";
	})
	btnReset.addEventListener('click', function () {
		inputs.value = "";
		inputs.focus();
		searchForm.classList.remove('active');
	})
});



